import { NgModule } from '@angular/core';
import { CommonModule } from '@angular/common';
import { AdminsDashboardComponent } from './admins-dashboard/admins-dashboard.component';
import { AdminsRoutingModule } from './admins-routing.module';
import { HeaderComponent } from './components/header/header.component';
import { MatButtonModule } from '@angular/material/button';
import {MatMenuModule} from '@angular/material/menu';
import { UsersComponent } from './admins-dashboard/pages/users/users.component';
import { TechnologiesComponent } from './admins-dashboard/pages/technologies/technologies.component';
import { CoursesComponent } from './admins-dashboard/pages/courses/courses.component';
import { InstructorsComponent } from './admins-dashboard/pages/instructors/instructors.component';
import { AddCourseFormComponent } from './admins-dashboard/pages/courses/forms/add-course-form/add-course-form.component';
import {MatSelectModule} from '@angular/material/select';
import {MatInputModule} from '@angular/material/input';
import {MatFormFieldModule} from '@angular/material/form-field';
import { ReactiveFormsModule } from '@angular/forms';
import { MatIconModule } from '@angular/material/icon';
import { NewTechFormComponent } from './admins-dashboard/pages/technologies/components/new-tech-form/new-tech-form.component';
import { CreateInstructorsFormComponent } from './admins-dashboard/pages/instructors/create-instructors-form/create-instructors-form.component';
import { SharedModule } from '../shared/shared.module';
import { CustomTableComponent } from './components/custom-table/custom-table.component';
import { MatTableModule } from '@angular/material/table';
import { MatDialogModule } from '@angular/material/dialog';




@NgModule({
  declarations: [
    AdminsDashboardComponent,
    HeaderComponent,
    UsersComponent,
    TechnologiesComponent,
    CoursesComponent,
    InstructorsComponent,
    AddCourseFormComponent,
    NewTechFormComponent,
    CreateInstructorsFormComponent,
    CustomTableComponent

  ],
  imports: [
    CommonModule,
    AdminsRoutingModule,
    ReactiveFormsModule,
    MatButtonModule,
    MatMenuModule,
    MatSelectModule,
    MatInputModule,
    MatFormFieldModule,
    MatIconModule,
    SharedModule,
    MatTableModule,
    MatDialogModule,

  ]
})
export class AdminsModule { }

import { NgModule } from '@angular/core';
import { RouterModule, Routes } from '@angular/router';
import { AdminsDashboardComponent } from './admins-dashboard/admins-dashboard.component';
import { UsersComponent } from './admins-dashboard/pages/users/users.component';
import { TechnologiesComponent } from './admins-dashboard/pages/technologies/technologies.component';
import { CoursesComponent } from './admins-dashboard/pages/courses/courses.component';
import { InstructorsComponent } from './admins-dashboard/pages/instructors/instructors.component';
import { ErrorPageComponent } from '../shared/components/default-pages/error-page/error-page.component';
import { WorkingInProgressComponent } from '../shared/components/default-pages/working-in-progress/working-in-progress.component';
import { RoleGuard } from '../shared/guards/role/role.guard';

const routes: Routes = [
    { path: '', component: AdminsDashboardComponent, canActivate:[RoleGuard], data: {expectedRole: 'admin'}, children: [
      { path: '', redirectTo: 'users', pathMatch: 'full' },
      // { path: 'users', component: UsersComponent },
      { path: 'users', component: UsersComponent },
      { path: 'technologies', component: TechnologiesComponent },
      { path: 'courses', component: CoursesComponent },
      { path: 'instructors', component: InstructorsComponent },
      { path: '**', component: ErrorPageComponent},

    ] },
];

@NgModule({
  imports: [RouterModule.forChild(routes)],
  exports: [RouterModule]
})
export class AdminsRoutingModule { }

import { NgModule } from '@angular/core';
import { BrowserModule } from '@angular/platform-browser';
import { AppRoutingModule } from './app-routing.module';
import { AppComponent } from './app.component';
import { BrowserAnimationsModule } from '@angular/platform-browser/animations';
import { NgbModule } from '@ng-bootstrap/ng-bootstrap';
import { StoreModule } from '@ngrx/store';
import { userReducer } from './login/store/user.reducer'; 
import { initializeApp, provideFirebaseApp } from '@angular/fire/app';
import { getAuth, provideAuth } from '@angular/fire/auth';
import { getFirestore, provideFirestore } from '@angular/fire/firestore';
import { environment } from 'src/environments/environment';
import { coursesReducer } from './store/courses/courses.reducer';
import { EffectsModule } from '@ngrx/effects';
import { CoursesEffects } from './store/courses/courses.effects';
import { technologiesReducer } from './store/technologies/technologies.reducer';
import { TechnologiesEffects } from './store/technologies/technologies.effects';
import { instructorsReducer } from './store/instructors/instructors.reducer';
import { InstructorsEffects } from './store/instructors/instructors.effects';
import { SharedModule } from './shared/shared.module';
import { usersReducer } from './store/users/users.reducer';
import { UsersEffects } from './store/users/users.effects';
import { courseReducer } from './admins/admins-dashboard/pages/courses/store/course/course.reducer';


 
@NgModule({
  declarations: [
    AppComponent,
    
    
  
  ],
  imports: [
    BrowserModule,
    BrowserAnimationsModule,
    NgbModule,
    SharedModule,
    StoreModule.forRoot({
      user: userReducer,
      courses: coursesReducer,
      technologies: technologiesReducer,
      instructors: instructorsReducer,
      users: usersReducer,
      course: courseReducer
    }),
    EffectsModule.forRoot(CoursesEffects, TechnologiesEffects, InstructorsEffects, UsersEffects),
    AppRoutingModule,
    provideFirebaseApp(() => initializeApp(environment.firebaseConfig)),
    provideFirestore(() => getFirestore()),
    provideAuth(() => getAuth())
  ],
  providers: [],
  bootstrap: [AppComponent]
})
export class AppModule { }

import { NgModule } from '@angular/core';
import { RouterModule, Routes } from '@angular/router';
import { ErrorPageComponent } from './shared/components/default-pages/error-page/error-page.component';

const routes: Routes = [
  { path: '', loadChildren: () => "import('./main-page/main-page.module').then(m => m.MainPageModule)"},
  { path: 'login', loadChildren: () => "import('./login/login.module').then(m => m.LoginModule)" },
  { path: 'students', loadChildren: () => "import('./students/students.module').then(m => m.StudentsModule)" },
  { path: 'admin', loadChildren: () => "import('./admins/admins.module').then(m => m.AdminsModule)" },
  { path: '**', component: ErrorPageComponent},

];

@NgModule({
  imports: [RouterModule.forRoot(routes)],
  exports: [RouterModule]
})
export class AppRoutingModule { }

